    padding: 0.25rem 0;
    line-height: 1.5;
    color: var(--color);
  }
  strong{
    font-weight: 500;
    color: white;
  }

  a {
    text-decoration: underline;
  }

  h1 {
    font-size: 2.25rem;
    font-weight: 500;
    margin-bottom:.5rem;
    color: white;
  }

  h2 {
    font-size: 1.75rem;
    font-weight: 500;
    margin-bottom: 0.5rem;
    margin-top: 2rem;
    color: white;
  }

  h3 {
    font-size: 1.5rem;
    font-weight: 500;
    margin-bottom: 0.25rem;
    margin-top: 1rem;
    color: white;
  }

  h4 {
    font-size: 1.15rem;
    font-weight: 500;
    margin-bottom: 0.25rem;
    margin-top: 1rem;
    color: white;
  }

  h5 {
    font-size: 1rem;
    font-weight: 500;
    margin-bottom: 0.25rem;
    margin-top: 1rem;
    color: white;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
