header {
  background: var(--background, white);
  border-bottom: 1px solid var(--border-color, #e5e5e7);
}

.section-container {
  padding-top: 1rem;
  padding-bottom: 1rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.logos {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: var(--color-accent);

  .is-primo svg {
    width: 7rem;
    color: white;
    transform: translateY(-1px);
  }

  a.link {
    color: var(--color);
    border-bottom: 2px solid transparent;
    transition: border-color 0.1s;
    span {
      font-size: 1rem;
      font-weight: 400;
    }

    &:first-child {
      color: var(--color-accent);
      padding-bottom: 3px;
      transform: translateY(5px);
    }

    &:hover {
      border-color: var(--color-accent);
      color: white;
    }
  }
}

nav {
  display: flex;
  align-items: center;
  gap: 2rem;

  a.link.active {
    border-bottom: 2px solid var(--color-accent);
    transition: border-color 0.1s;
    font-size: 1rem;

  }

  a.link {
    display: none;

    &:hover{
      color: white;
      transition: .3s;
    }
  }

  button#close {
    display: none;
  }
}

.call-to-action {
  display: flex;
  align-items: center;
  gap: 1.5rem;

  #theme {
    svg {
      width: 1.25rem;
      height: 1.25rem;
    }
  }

  :global(.locale-selector select) {
    background: transparent;
  }
}

nav#mobile-nav {
  position: absolute;
  flex-direction: column;
  text-align: center;
  background: var(--background);
  box-shadow: var(--box-shadow);
  border-radius: var(--border-radius);
  left: 0.5rem;
  top: 0.5rem;
  right: 0.5rem;
  padding: 2rem;
  z-index: 9;

  a.link {
    display: block;
  }

  /*   Button */
  /*   a:last-of-type {
    color: var(--button-color, white);
    background: var(--button-background, #154bf4);
    border-radius: var(--button-border-radius, 5px);
    padding: var(--button-padding, 8px 20px);
    width: 100%;
    justify-content: center;
  } */

  button#close {
    display: block;
    position: absolute;
    right: 1rem;
    top: 1rem;
    svg {
      color: var(--color-accent, #154bf4);
      width: 1.5rem;
      height: 1.5rem;
    }
  }
}

@media (min-width: 900px) {
  .call-to-action {
    display: none;
  }
  #open,
  #mobile-nav {
    display: none;
  }
  nav {
    a.link {
      display: inline-block;
      border-bottom: 2px solid transparent;
      transition: border-color 0.1s;

      &:hover {
        border-color: var(--color-accent);
      }
    }
  }
}
