value.css:
nav {
  padding: 3rem 0;
  display: flex;
  justify-content: center;
  flex-direction: column;
  text-align: center;

  .logo {
    width: 3rem;
    transition: opacity 0.1s;
    align-self: center;
    margin-bottom: 1rem;

    &:hover {
      opacity: 0.8;
    }
  }

  a {
    font-weight: 600;
  }

  span {
    display: none;
    margin: 0 2rem;
  }
}

@media (min-width: 600px) {
  nav {
  	flex-direction: row;
    justify-content: center;

    .logo {
    	margin-right: 1.5rem;
    }

    span {
    	display: inline;		
    }
  }
}
